                    p1.PrintPlayer();
                    e1.PrintEnemy();
                    e1.PrintEnemy2();
                    e1.PrintEnemy3();
                    while (true)
                    {
                        if (Keyboard.IsKeyPressed(Key.UpArrow))
                        {
                            p1.MovePlayerUp();
                        }
                        else if (Keyboard.IsKeyPressed(Key.DownArrow))
                        {
                            p1.MovePlayerDown();
                        }
                        else if (Keyboard.IsKeyPressed(Key.LeftArrow))
                        {
                            p1.MovePlayerLeft();
                        }
                        else if (Keyboard.IsKeyPressed(Key.RightArrow))
                        {
                            p1.MovePlayerRight();
                        }

                        e1.MoveEnemy();
                        e1.MoveEnemy2();
                        e1.MoveEnemy3();
                    }
                }
