        public static void Instructions()
        {
            Console.WriteLine();
            Console.WriteLine("Instructions to play the game:");
            Console.WriteLine();
            Console.SetCursorPosition(65, 24);
            Console.WriteLine("1) Use arrow keys to move the Tank.");
            Console.SetCursorPosition(65, 25);
            Console.WriteLine("2) Use space bar to shoot bullets.");
            Console.SetCursorPosition(65, 26);
            Console.WriteLine("3) If you collide with enemy, you will lose 5 health points.");
            Console.SetCursorPosition(65, 27);
            Console.WriteLine("4) Kill Both enemies to attain victory .");
            Console.SetCursorPosition(65, 28);
            Console.WriteLine("5) On every hit enemy will lose 1 health point.");
        }


        public static void ClearScreen()
        {
            Console.SetCursorPosition(65, 40);
            Console.Write("Enter any key to continue");
            Console.ReadKey();
            Console.Clear();
            print_header();
        }

        
        static void printMaze(char[,] maze)
        {
            for (int i = 0; i < 42; i++)
            {
                for (int j = 0; j < 144; j++)
                {
                    Console.Write(maze[i, j]);
                }
                Console.WriteLine(" ");
            }
        }

